core.microhandlers.add('assignRecommendedModules','.recommended-modules',function(){const modulesBlock=jQuery(this);jQuery(function(){const src=modulesBlock.data('src');if(src)modulesBlock.append('<iframe src="'+src+'"/>');modulesBlock.find('iframe').load(function(){jQuery(this).contents().on('recommendedModulesShown',function(event){modulesBlock.removeClass('hidden')})})})})